SELECT [Departmental].[last name], [Departmental].[first name], [Departmental].[adp number], [Departmental].[import id], [Childcare Issues].[Childcare Issues], [Comp Day].[Comp Day], [Departmental].[Departmental], [Doctor Appointment].[Doctor Appointment], [Family Medical Leave].[Family Medical Leave], [Field Trip].[Field Trip], [Funeral/Death].[Funeral/Death], [Illness].[Illness], [Illness (Child)].[Illness (Child)], [Illness (Family)].[Illness (Family)], [Jury Duty].[Jury Duty], [Kairos].[Kairos], [Long Term Disability].[Long Term Disability], [New Baby].[New Baby], [Personal].[Personal], [Professional].[Professional], [Retreat].[Retreat], [Short Term Disability].[Short Term Disability], [Sports].[Sports], [Tardy].[Tardy], [Unknown Cause].[Unknown Cause], [Vacation].[Vacation]
FROM (((((((((((((((((((((
	(SELECT f.[last name], f.[first name], f.[ADP Number], f.[import id], SUM(hours) as [Childcare Issues] FROM [RHS Faculty] as f LEFT JOIN [Attendance] as a ON f.[import id] = a.[import id] WHERE code = 'Childcare Issues' GROUP BY f.[ADP Number], f.[last name], f.[first name], f.[import id])
	AS [Childcare Issues]
INNER JOIN (SELECT f.[last name], f.[first name], f.[ADP Number], f.[import id], SUM(hours) as [Comp Day] FROM [RHS Faculty] as f LEFT JOIN [Attendance] as a ON f.[import id] = a.[import id] WHERE code = 'Comp Day' GROUP BY f.[ADP Number], f.[last name], f.[first name], f.[import id]) AS [Comp Day] ON [Childcare Issues].[import id] = [Comp Day].[import id])
INNER JOIN (SELECT f.[last name], f.[first name], f.[ADP Number], f.[import id], SUM(hours) as [Departmental] FROM [RHS Faculty] as f LEFT JOIN [Attendance] as a ON f.[import id] = a.[import id] WHERE code = 'Departmental' GROUP BY f.[ADP Number], f.[last name], f.[first name], f.[import id]) AS [Departmental] ON [Comp Day].[import id] = [Departmental].[import id])
INNER JOIN (SELECT f.[last name], f.[first name], f.[ADP Number], f.[import id], SUM(hours) as [Doctor Appointment] FROM [RHS Faculty] as f LEFT JOIN [Attendance] as a ON f.[import id] = a.[import id] WHERE code = 'Doctor Appointment' GROUP BY f.[ADP Number], f.[last name], f.[first name], f.[import id]) AS [Doctor Appointment] ON [Departmental].[import id] = [Doctor Appointment].[import id])
INNER JOIN (SELECT f.[last name], f.[first name], f.[ADP Number], f.[import id], SUM(hours) as [Family Medical Leave] FROM [RHS Faculty] as f LEFT JOIN [Attendance] as a ON f.[import id] = a.[import id] WHERE code = 'Family Medical Leave' GROUP BY f.[ADP Number], f.[last name], f.[first name], f.[import id]) AS [Family Medical Leave] ON [Doctor Appointment].[import id] = [Family Medical Leave].[import id])
INNER JOIN (SELECT f.[last name], f.[first name], f.[ADP Number], f.[import id], SUM(hours) as [Field Trip] FROM [RHS Faculty] as f LEFT JOIN [Attendance] as a ON f.[import id] = a.[import id] WHERE code = 'Field Trip' GROUP BY f.[ADP Number], f.[last name], f.[first name], f.[import id]) AS [Field Trip] ON [Family Medical Leave].[import id] = [Field Trip].[import id])
INNER JOIN (SELECT f.[last name], f.[first name], f.[ADP Number], f.[import id], SUM(hours) as [Funeral/Death] FROM [RHS Faculty] as f LEFT JOIN [Attendance] as a ON f.[import id] = a.[import id] WHERE code = 'Funeral/Death' GROUP BY f.[ADP Number], f.[last name], f.[first name], f.[import id]) AS [Funeral/Death] ON [Field Trip].[import id] = [Funeral/Death].[import id])
INNER JOIN (SELECT f.[last name], f.[first name], f.[ADP Number], f.[import id], SUM(hours) as [Illness] FROM [RHS Faculty] as f LEFT JOIN [Attendance] as a ON f.[import id] = a.[import id] WHERE code = 'Illness' GROUP BY f.[ADP Number], f.[last name], f.[first name], f.[import id]) AS [Illness] ON [Funeral/Death].[import id] = [Illness].[import id])
INNER JOIN (SELECT f.[last name], f.[first name], f.[ADP Number], f.[import id], SUM(hours) as [Illness (Child)] FROM [RHS Faculty] as f LEFT JOIN [Attendance] as a ON f.[import id] = a.[import id] WHERE code = 'Illness (Child)' GROUP BY f.[ADP Number], f.[last name], f.[first name], f.[import id]) AS [Illness (Child)] ON [Illness].[import id] = [Illness (Child)].[import id])
